== false) {    $acreed = explode("S.C.C.",$model->nombreAcreedor);  }  else{    $acreed = array($model->nombreAcreedor);     }  $html='    <table id="yw0" class="detail-view2">             <tr>        <td><a href="https://subastas.boe.es/detalleSubasta.php?idSub='.$model->identificador.'&ver=1" target="_blank"><strong style="font-size: 10px; color: black;"> '.$ident[1].' </strong></a></td>        <td><strong style="font-size: 10px;"> '.$fecha.' </strong></td>        <td><strong style="font-size: 10px;"> '.$tas[0].' </strong></td>        <td><strong style="font-size: 10px;"> '.$cReclam[0].' </strong></td>        <td><strong style="font-size: 10px;"> '.$coef[0].' </strong></td>        <td><strong style="font-size: 10px;"> '.$model->localidad.' </strong></td>        <td><strong style="font-size: 10px;"> '.$model->codigoPostal.' </strong></td>        <td><strong style="font-size: 10px;"> '.$model->direccion.' </strong></td>        <td><strong style="font-size: 10px;"> '.$model->acreedor.' </strong></td>        <td><strong style="font-size: 10px;"> '.$acreed[0].' </strong></td>     </tr>    </table>    ';$mpdf->WriteHTML($html); }$mpdf->Output('Subastas.pdf','D');exit;?>